package com.pattern.learning;

public class Pattern {

	public static void main(String[] args) {
		int n=3;
		// TODO Auto-generated method stub
		for(int i=1;i<=n;i++)
		{
			for(int j=1;j<=n;j++)
			{
				if(i>=j)
				{ System.out.print(i);
			}
			}
			System.out.println();
		}

	}

}
